import { useState } from 'react';
import { auth, provider } from '../firebase';
import { signup } from '../api';
import { signInWithPopup, GoogleAuthProvider, createUserWithEmailAndPassword } from 'firebase/auth';

const AuthForm = ({ setUser }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [profilePhoto, setProfilePhoto] = useState('');
  const [coverPhoto, setCoverPhoto] = useState('');
  
  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;
      const userData = { name, email: user.email, phone, profilePhoto, coverPhoto };
      const response = await signup(userData);
      setUser(response);
    } catch (error) {
      console.error(error);
    }
  };

  const handleGoogleSignup = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      const user = result.user;
      const userData = { name: user.displayName, email: user.email, phone, profilePhoto, coverPhoto };
      const response = await signup(userData);
      setUser(response);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleSignup} className="p-4">
      <input type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="Name" required />
      <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" required />
      <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" required />
      <input type="text" value={phone} onChange={(e) => setPhone(e.target.value)} placeholder="Phone" />
      <input type="text" value={profilePhoto} onChange={(e) => setProfilePhoto(e.target.value)} placeholder="Profile Photo URL" />
      <input type="text" value={coverPhoto} onChange={(e) => setCoverPhoto(e.target.value)} placeholder="Cover Photo URL" />
      <button type="submit">Sign Up</button>
      <button type="button" onClick={handleGoogleSignup}>Sign Up with Google</button>
    </form>
  );
};

export default AuthForm;
