import { useRef } from 'react';
import imgae_ineractive from '../images/OIP.jpeg'

const InteractiveImage = () => {
  const imgRef = useRef(null);
  

  const handleMouseMove = (e) => {
    const { clientX, clientY } = e;
    const { offsetWidth, offsetHeight } = imgRef.current;
    const x = (clientX / offsetWidth) * 100;
    const y = (clientY / offsetHeight) * 100;

    imgRef.current.style.transform = `translate(-${x}%, -${y}%)`;
  };

  return (
    <div className="relative w-full h-64 overflow-hidden" onMouseMove={handleMouseMove}>
      <img
        ref={imgRef}
        src={imgae_ineractive}
        alt="Interactive"
        className="absolute top-0 left-0 w-full h-full object-cover transition-transform duration-500"
      />
    </div>
  );
};

export default InteractiveImage;
